import React from 'react';
import './MovieCardProfile.scss'; // Adjust path based on your project structure
import Button from '../../button/Button'; // Adjust path based on your project structure

const MovieCardProfile = ({ item, onRemove }) => {
    const link = `/${item.id}`; // Assuming you have a link setup
    const bg = item.poster_path ? `https://image.tmdb.org/t/p/w500${item.poster_path}` : '';

    const handleRemove = () => {
        onRemove(item.id); // Assuming item.id uniquely identifies the movie
    };

    return (
        <div className="movie-card-profile" style={{backgroundImage: `url(${bg})`}}>
            <div className="overlay">
                <Button onClick={handleRemove} className="remove-button">
                    Remove
                </Button>
            </div>
            <h3>{item.title || item.name}</h3>
        </div>
    );
};

export default MovieCardProfile;
