// src/pages/Profile.js
import React, { useState, useEffect } from 'react';
import './Profile.scss'; // Import the SCSS file
import MovieCard from '../components/movie-card/MovieCard'; // Import the MovieCard component
import tmdbApi from '../../api/tmdbApi'; // Import the tmdbApi for API calls

const Profile = ({ username = 'User', favoriteMovies = [] }) => {
    const [moviesData, setMoviesData] = useState([]);

    useEffect(() => {
        const fetchMoviesData = async () => {
            try {
                const moviesDataPromises = favoriteMovies.map(async movieName => {
                    const params = { query: movieName };
                    const response = await tmdbApi.search('movie', { params });
                    return response.data.results[0]; // Assuming the first result is the desired movie
                });

                const moviesDataResults = await Promise.all(moviesDataPromises);
                setMoviesData(moviesDataResults);
            } catch (error) {
                console.error('Error fetching movie data:', error);
            }
        };

        fetchMoviesData();
    }, [favoriteMovies]);

    return (
        <div className="profile-container">
            <div className="profile-header">
                <h1>{username}'s Profile</h1>
                <button className="edit-button">Edit Profile</button>
            </div>
            <div className="profile-content">
                <h2>Favorite Movies</h2>
                <div className="movie-cards">
                    {moviesData.length > 0 ? (
                        moviesData.map((movie, index) => (
                            <MovieCard key={index} item={movie} category="movie" />
                        ))
                    ) : (
                        <p className="no-movies">Loading...</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Profile;
